export const sendWhatsAppMessage = (phone, product) => {
  const message = `
Hello,
I am interested in this product:

Name: ${product.name}
Price: ${product.price}
`;

  const url = `https://wa.me/${phone}?text=${encodeURIComponent(message)}`;
  window.open(url, "_blank");
};
