import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { db } from "../api/firebase";
import { ref, get } from "firebase/database";
import "../styles/giftDetails.css";

export default function GiftDetails() {
  const { categoryId, giftId } = useParams();

  const [gift, setGift] = useState(null);

  useEffect(() => {
    const load = async () => {
      const snap = await get(
        ref(db, `giftkart/${categoryId}/${giftId}`)
      );

      if (snap.exists()) {
        setGift(snap.val());
      }
    };

    load();
  }, [categoryId, giftId]);

  if (!gift) return <h2 style={{textAlign:"center"}}>Loading...</h2>;

  return (
    <div className="gift-details-container">

      {/* IMAGE GALLERY */}
      <div className="gift-images">
        <img src={gift.Image1} alt="" />
        {gift.image2 && <img src={gift.image2} alt="" />}
        {gift.image3 && <img src={gift.image3} alt="" />}
      </div>

      {/* TEXT INFO */}
      <div className="gift-info">
        <h1>{gift.Tittle}</h1>

        <p><b>For:</b> {gift.reciever}</p>

        <p>{gift.Message}</p>

        <div className="gift-card-box">
          <p><b>Greeting:</b> {gift.Greetings}</p>
          <p><b>From:</b> {gift.Name}</p>
        </div>

      </div>

    </div>
  );
}
