import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { db } from "../api/firebase";
import { ref, get } from "firebase/database";

import "../styles/giftDetailsPremium.css";

export default function GiftDetails() {
  const { categoryId, giftId } = useParams();
  const [gift, setGift] = useState(null);

  useEffect(() => {
    const load = async () => {
      const snap = await get(
        ref(db, `giftkart/${categoryId}/${giftId}`)
      );

      if (snap.exists()) setGift(snap.val());
    };

    load();
  }, [categoryId, giftId]);

  if (!gift) return <h2 className="loading">Loading Gift...</h2>;

  /* CATEGORY BASED GREETING */
  const categoryGreeting = () => {
    if (categoryId === "birthday") return "üéâ Happy Birthday üéâ";
    if (categoryId === "anniversary") return "üíñ Happy Anniversary üíñ";
    if (categoryId === "valentine") return "‚ù§Ô∏è Happy Valentine ‚ù§Ô∏è";
    return "üéÅ Special Gift üéÅ";
  };

  return (
    <div
      className="gift-bg"
      style={{ backgroundImage: `url(${gift.Image1})` }}
    >
      <div className="gift-overlay">

        {/* FLOATING GIFT CARD */}
        <div className="gift-card">

          <h2 className="category-text">
            {categoryGreeting()}
          </h2>

          <h1 className="gift-title">{gift.Tittle}</h1>

          <p className="gift-receiver">
            For: {gift.reciever}
          </p>

          <div className="gift-message-box">
            <p>{gift.Message}</p>
          </div>

          <div className="gift-footer">
            <p className="gift-greeting">
              {gift.Greetings}
            </p>
            <p className="gift-from">
              ‚Äî {gift.Name}
            </p>
          </div>

        </div>

      </div>
    </div>
  );
}
