export const subscribeGifts = (callback) => {
  const giftsRef = ref(db, "gifts");

  return onValue(giftsRef, (snapshot) => {
    console.log("Firebase Data:", snapshot.val());

    const data = snapshot.val() || {};

    const giftArray = Object.keys(data).map((key) => ({
      id: key,
      ...data[key],
    }));

    callback(giftArray);
  });
};
